package com.shakir;

public class Operators {
	
	public static void main(String[] args) {
		//TODO Auto-generated method stub
		//operators include- && || = 
		//comparison operators- <= >= ==
		//incremental and descremental operators- ++ -- (++num1 - preincrement/predescrement , num1++ - postincrement/postdescrement
		//the operation is followed by the assignment
		int num1=10;
		int num2=50;
		System.out.println(num1++) ; //10
		System.out.println(num1) ; //11
		//System.out.println("line13"+num1--) ; 
		//System.out.println("line14"+num1) ;
		System.out.println(++num1) ;
		System.out.println(++num1) ;
		System.out.println(--num1);
		System.out.println(num2 + num1);
	
		
		
		
		
	}
	

}
